import math as m

def DecodePacket(pack):
    print(f'---- NEW PACKET {int(pack[:3],2)} ----')
    print('parsed: ',pack)
    print('lenght: ',len(pack))
    

    version = int(pack[:3],2); pack = pack[3:]
    print('version: ',version)
    typeID = int(pack[:3],2); pack = pack[3:]
    
    print('type ID: ',typeID)
    if(int(typeID) == 4):
        literal = ''
        while(pack[0]=='1'):
            literal += pack[1:5]; pack = pack[5:]
        literal += pack[1:5]; pack = pack[5:]
        print(literal)
        #return(int(literal,2))
        literal = int(literal,2)
        print('literal output: ',literal)

        print('left over: ', pack)
        print(f'--- END PACKET {version} ---')
        return version, pack
             
    else:
        packetsSum=0
        print('lenght ID type: ',pack[0])
        # Check the lenght type ID bit
        if(pack[0]=='0'):
            
            pack = pack[1:]
            print(pack[:15], end=' : ')
            lenght = int(pack[:15], 2); pack = pack[15:]
            print(lenght)
            
            while lenght>0:
                out, o =  DecodePacket(pack); #pack = pack[lenght:]
                lenght-=len(pack) - len(o)
                
                pack = o
                
                
                packetsSum += out
        else: # lenght type ID == 1
            pack = pack[1:]

            quantity = int(pack[:11], 2); pack = pack[11:]
            print('quantity: ',quantity)
            while quantity>0:
                out,pack = DecodePacket(pack)
                packetsSum+=out
                quantity-=1


        
        print('left over: ', pack)
        print(f'--- END PACKET {version} ---')
        return packetsSum+version, pack


    

with open('packets.txt','r') as f:
    for line in f:
        line = line.strip('\n')

        line = ''.join([ str(bin(int(line[i], 16)))[2:].zfill(4) for i in range(len(line)) ])#.rstrip('0')
        input(('input:',line))
        sum,_ = DecodePacket(line)
        print('output: ',sum)


